mapper.select:
SELECT agent_id, company_id, name, phone_number, state, email, address, role, created_at, profile_image_url, email_verified
FROM agents
WHERE agent_id = #{id}

SELECT agent_id, company_id, name, email, password, phone_number, address, role, state, created_at, profile_image_url, email_verified
FROM agents
WHERE email = #{email}

SELECT agent_id, company_id, name, phone_number, state, email, address, role, created_at, profile_image_url, email_verified
FROM agents


        AND name = #{name}


        AND email = #{email}


        AND state = #{state}

mapper.update:
UPDATE agents
SET state = #{state}
WHERE agent_id = #{id}

UPDATE agents
SET password = #{password}
WHERE agent_id = #{agentId}

mapper.select:
SELECT COUNT(*) > 0
FROM agents
WHERE email = #{email}

mapper.update:
UPDATE agents
SET email_verified = #{verified}
WHERE email = #{email}
