mapper.select:
SELECT COUNT(*)
FROM contract
WHERE client_id = #{clientId}
AND contract_time = #{contractTime}

SELECT COUNT(*)
FROM contract
WHERE agent_id = #{agentId}
AND contract_time = #{contractTime}

SELECT
COUNT(*)
FROM contract
WHERE client_id = #{clientId}
AND contract_time =
#{contractTime}
AND contract_id != #{excludeContractId}

